var jsonUtil = require('./jsonUtil');


function getConnectionList() {
	var dirname = '.';
	var file = 'info_jdbc.json';
	var encoding = 'utf8';
	var data = jsonUtil.readJsonFileFromDir(dirname, file, encoding);
  return data;
}

function getConnectionJdbc(callback) {
	var dirname = '.';
	var file = 'connection_jdbc.json';
	var encoding = 'utf8';
	jsonUtil.readJsonFileFromDir(dirname, file, encoding, callback);
}


function getPlateformPays() {
	var dirname = '.';
	var file = 'db_pays_plateform.json';
	var encoding = 'utf8';
	var data = jsonUtil.readJsonFileFromDir(dirname, file, encoding);
	//console.log(data);
	return data;
}


function onError(err) {
  console.error('FAIL: ' + err.message);
}

exports.getPlateformPays = getPlateformPays;
exports.getConnectionList = getConnectionList;
exports.getConnectionJdbc = getConnectionJdbc;
exports.onError = onError;
